import React from "react";
import ReactDOM from "react-dom/client";
import {Provider} from "react-redux";
import store from "./store/index";
import "../styles/main.scss";
import Index from "./app";
import {BrowserRouter} from "react-router-dom";
import SupportModule from "./app/SupportModule";


function ReactTalk(props) {
  return (
    <Provider store={store}>
      <BrowserRouter>
        <SupportModule supportMode={props && props.supportMode}>
          <Index {...props}/>
        </SupportModule>
      </BrowserRouter>
    </Provider>
  );
}

function Talk(props, elementId, callBack) {

  const root = ReactDOM.createRoot(document.getElementById(elementId));
  root.render(
    <Provider store={store}>
      <BrowserRouter>
        <SupportModule chatSupportAutoShowing={props && props.chatSupportAutoShowing} supportMode={props && props.supportMode}>
          <Index {...props} wrappedComponentRef={callBack}/>
        </SupportModule>
      </BrowserRouter>
    </Provider>)
}

function DestroyTalk(elementId) {
  ReactDOM.unmountComponentAtNode(document.getElementById(elementId));
}

export {ReactTalk, Talk};

window.Talk = Talk;
window.DestroyTalk = DestroyTalk;